      }
    ];

    categories.forEach(category => {
      this.createServiceCategory(category);
    });

    // Initialize professionals
    const professionals: InsertProfessional[] = [
      {
        name: "Rahul Sharma",
        email: "rahul@servicehub.com",
        phone: "+91 9876543210",
        specialization: "Cleaning",
        experience: 5,
